#!/bin/bash# Install Javasudo yum update -ysudo amazon-linux-extras enable corretto8sudo yum install -y java-1.8.0-amazon-corretto# Create Minecraft server directorymkdir -p /home/ec2-user/minecraftcd /home/ec2-user/minecraft# Download Minecraft server .jarwget https://launcher.mojang.com/v1/objects/fe62e8a0ed6f4aa8f3dc9b43276943dfaa30b639/server.jar -O server.jar# Accept EULAecho "eula=true" > eula.txt# Create systemd servicesudo tee /etc/systemd/system/minecraft.service > /dev/null <<EOF[Unit]Description=Minecraft ServerAfter=network.target[Service]WorkingDirectory=/home/ec2-user/minecraftExecStart=/usr/bin/java -Xmx1024M -Xms1024M -jar server.jar noguiUser=ec2-userRestart=on-failure[Install]WantedBy=multi-user.targetEOF# Start and enable the Minecraft servicesudo systemctl daemon-reexecsudo systemctl daemon-reloadsudo systemctl enable minecraftsudo systemctl start minecraft